// src/components/MenuList.jsx
import React from "react";

const iconForDiet = {
  vegan: "üå±",
  vegetarian: "ü•ó",
  glutenFree: "üö´üåæ",
};

const matchesFilters = (item, filters) => {
  const dietary = item["Dietary Tags"]?.toLowerCase() || "";
  const allergens = item["Allergens"]?.toLowerCase() || "";

  for (let key in filters) {
    if (filters[key]) {
      if (["vegan", "vegetarian", "glutenFree"].includes(key)) {
        if (!dietary.includes(key)) return false;
      } else {
        if (allergens.includes(key)) return false;
      }
    }
  }
  return true;
};

export default function MenuList({ items, filters }) {
  const grouped = {};
  items.forEach((item) => {
    const cat = item.Category || "Other";
    if (!grouped[cat]) grouped[cat] = [];
    if (matchesFilters(item, filters)) grouped[cat].push(item);
  });

  return (
    <div className="px-4 pb-20">
      {Object.entries(grouped).map(([cat, dishes]) => (
        <div key={cat} className="mb-6">
          <h2 className="text-lg font-semibold text-gray-800 mb-3">{cat}</h2>
          <div className="space-y-4">
            {dishes.map((dish) => (
              <div
                key={dish.Name}
                className="border rounded-lg p-4 shadow-sm bg-white"
              >
                <div className="flex justify-between items-center">
                  <h3 className="text-lg font-semibold">{dish.Name}</h3>
                  <span className="text-sm font-medium text-gray-700">‚Ç¨{dish.Price}</span>
                </div>
                <p className="text-sm text-gray-500">{dish.Ingredients}</p>
                <div className="mt-2 text-sm">
                  {dish["Dietary Tags"] && (
                    <div>
                      <span className="font-medium text-green-700">Dietary: </span>
                      {dish["Dietary Tags"]
                        .split(",")
                        .map((tag) => tag.trim().toLowerCase())
                        .map((tag) => (
                          <span key={tag} className="mr-2">
                            {iconForDiet[tag] || ""} {tag}
                          </span>
                        ))}
                    </div>
                  )}
                  {dish.Allergens && (
                    <div className="text-red-600">
                      ‚ö†Ô∏è Allergens: {dish.Allergens}
                    </div>
                  )}
                  {dish["Spicy Level"] && (
                    <div>üå∂Ô∏è Spicy Level: {dish["Spicy Level"]}</div>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}
