import React, { useState, useEffect, useRef } from "react";
import { QRCodeSVG } from "qrcode.react";

const QRCodeGenerator = () => {
  const [open, setOpen] = useState(false);
  const bubbleRef = useRef();
  const currentUrl = window.location.href;

  // Close popup when clicking outside
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (bubbleRef.current && !bubbleRef.current.contains(e.target)) {
        setOpen(false);
      }
    };
    if (open) {
      document.addEventListener("mousedown", handleClickOutside);
    }
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [open]);

  return (
    <div className="relative">
      {/* Trigger Button */}
      <button
        onClick={() => setOpen((prev) => !prev)}
        className="px-4 py-2 bg-green-600 text-white rounded-lg shadow-md hover:bg-green-700"
      >
        ðŸ“± Show QR Code
      </button>

      {/* Popup Bubble */}
      {open && (
        <div
          ref={bubbleRef}
          className="absolute bottom-12 left-1/2 transform -translate-x-1/2 bg-white p-4 rounded-xl shadow-xl border w-64 z-50"
        >
          <h2 className="text-center font-semibold mb-2">Scan to Open</h2>
          <div className="flex justify-center">
            <QRCodeSVG value={currentUrl} size={160} />
          </div>
          <button
            onClick={() => setOpen(false)}
            className="block mx-auto mt-4 px-3 py-1 text-sm text-gray-600 hover:text-black"
          >
            Close
          </button>
        </div>
      )}
    </div>
  );
};

export default QRCodeGenerator;
