// src/components/FilterPanel.jsx
import React from "react";

const dietaryIcons = {
  vegan: "ðŸŒ±",
  vegetarian: "ðŸ¥—",
  glutenfree: "ðŸš«ðŸŒ¾",
};

const allergenIcons = {
  Celery: "ðŸ¥¬",
  Gluten: "ðŸŒ¾",
  Crustaceans: "ðŸ¦",
  Eggs: "ðŸ¥š",
  Fish: "ðŸŸ",
  Lupin: "ðŸŒ¸",
  Milk: "ðŸ¥›",
  Molluscs: "ðŸš",
  Mustard: "ðŸŒ¿",
  Nuts: "ðŸŒ°",
  Peanuts: "ðŸ¥œ",
  Sesame: "âšª",
  Soy: "ðŸŒ±",
  Sulphites: "ðŸ’¨",
};

const allergensList = Object.keys(allergenIcons);

export default function FilterPanel({ filters, setFilters, safeOnly, setSafeOnly }) {
  const toggle = (key) => {
    setFilters((prev) => ({ ...prev, [key]: !prev[key] }));
  };

  const onGo = () => {
    setSafeOnly(true);
  };

  return (
    <div className="bg-white border-y py-4 px-4 space-y-6 shadow-sm">
      {/* Dietary Filters */}
      <div>
        <h3 className="font-semibold text-gray-800 mb-2">Dietary Preferences</h3>
        <div className="flex gap-2 flex-wrap">
          {["vegan", "vegetarian", "glutenfree"].map((key) => (
            <label
              key={key}
              className={`px-3 py-2 rounded-full border shadow-sm cursor-pointer ${
                filters[key] ? "bg-green-100 border-green-400" : "bg-gray-100"
              }`}
            >
              <input
                type="checkbox"
                className="hidden"
                checked={filters[key]}
                onChange={() => toggle(key)}
              />
              <span>{dietaryIcons[key]} {key}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Allergen Filters */}
      <div>
        <h3 className="font-semibold text-gray-800 mb-2">Avoid Allergens</h3>
        <div className="flex gap-2 flex-wrap">
          {allergensList.map((key) => (
            <label
              key={key}
              className={`px-3 py-2 rounded-full border shadow-sm cursor-pointer text-sm ${
                filters[key] ? "bg-red-100 border-red-400" : "bg-gray-100"
              }`}
            >
              <input
                type="checkbox"
                className="hidden"
                checked={filters[key]}
                onChange={() => toggle(key)}
              />
              <span>{allergenIcons[key]} {key}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Let's Go Button */}
      <div className="text-center pt-2">
        <button
          onClick={onGo}
          className="bg-teal-600 text-white px-8 py-2 rounded-full shadow hover:bg-teal-700 transition"
        >
          âœ… Letâ€™s Go
        </button>
      </div>
    </div>
  );
}
