// src/pages/App.jsx
import React, { useState, useEffect } from "react";
import DisclaimerModal from "../components/DisclaimerModal";
import LocationSelector from "../components/LocationSelector";
import MenuTabs from "../components/MenuTabs";
import FilterPanel from "../components/FilterPanel";
import MenuList from "../components/MenuList";
import { fetchMenu } from "../utils/sheetsAPI";

const App = () => {
  const [accepted, setAccepted] = useState(false);
  const [location, setLocation] = useState(null);
  const [tab, setTab] = useState("Food");
  const [menu, setMenu] = useState([]);
  const [filters, setFilters] = useState({
    vegan: false,
    vegetarian: false,
    glutenFree: false,
    gluten: false,
    nuts: false,
    dairy: false,
    soy: false,
    shellfish: false
  });

  useEffect(() => {
    if (accepted && location) {
      fetchMenu(tab).then(data => setMenu(data));
    }
  }, [accepted, location, tab]);

  if (!accepted) return <DisclaimerModal onAccept={() => setAccepted(true)} />;
  if (!location) return <LocationSelector onSelect={setLocation} />;

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow p-4 text-center text-2xl font-bold text-teal-700">
        SafeBite â€“ {location}
      </header>
      <MenuTabs selectedTab={tab} setTab={setTab} />
      <FilterPanel filters={filters} setFilters={setFilters} />
      <MenuList items={menu} filters={filters} />
    </div>
  );
};

export default App;
