import React from "react";
import { FaSearch } from "react-icons/fa";

const SearchBar = ({ search, setSearch, compact }) => {
  const [showInput, setShowInput] = React.useState(!compact);

  return compact ? (
    <div className="relative">
      <button
        className="text-gray-600"
        onClick={() => setShowInput((prev) => !prev)}
      >
        <FaSearch />
      </button>
      {showInput && (
        <input
          type="text"
          placeholder="Search..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="absolute top-8 right-0 px-3 py-1 border rounded shadow text-sm"
        />
      )}
    </div>
  ) : (
    <div className="px-4 mt-4">
      <input
        type="text"
        placeholder="Search..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="w-full px-4 py-2 border rounded-lg shadow-sm"
      />
    </div>
  );
};

export default SearchBar;
