// src/components/FilterPanel.jsx
import React from "react";

export default function FilterPanel({ filters, setFilters }) {
  const toggle = (key) => {
    setFilters((prev) => ({ ...prev, [key]: !prev[key] }));
  };

  return (
    <div className="bg-white border-t border-b py-4 px-4 space-y-4 shadow-sm">
      <div>
        <h3 className="font-semibold text-gray-800 mb-2">Dietary Preferences</h3>
        <div className="flex gap-4 flex-wrap">
          {["vegan", "vegetarian", "glutenFree"].map((key) => (
            <label key={key} className="flex items-center gap-2">
              <input
                type="checkbox"
                checked={filters[key]}
                onChange={() => toggle(key)}
              />
              <span className="capitalize">{key}</span>
            </label>
          ))}
        </div>
      </div>
      <div>
        <h3 className="font-semibold text-gray-800 mb-2">Allergens</h3>
        <div className="flex gap-4 flex-wrap">
          {["gluten", "nuts", "dairy", "soy", "shellfish"].map((key) => (
            <label key={key} className="flex items-center gap-2">
              <input
                type="checkbox"
                checked={filters[key]}
                onChange={() => toggle(key)}
              />
              <span className="capitalize">{key}</span>
            </label>
          ))}
        </div>
      </div>
    </div>
  );
}
