// src/components/MenuList.jsx
import React from "react";

const dietaryIcons = {
  vegan: "üå±",
  vegetarian: "ü•ó",
  glutenfree: "üö´üåæ",
};

const allergenIcons = {
  Celery: "ü•¨",
  Gluten: "üåæ",
  Crustaceans: "ü¶ê",
  Eggs: "ü•ö",
  Fish: "üêü",
  Lupin: "üå∏",
  Milk: "ü•õ",
  Molluscs: "üêö",
  Mustard: "üåø",
  Nuts: "üå∞",
  Peanuts: "ü•ú",
  Sesame: "‚ö™",
  Soy: "üå±",
  Sulphites: "üí®",
};

const matchesFilters = (item, filters) => {
  const dietary = (item["Dietary Tags"] || "").toLowerCase();
  const allergens = (item.Allergens || "").toLowerCase();

  for (let key in filters) {
    if (filters[key]) {
      if (["vegan", "vegetarian", "glutenfree"].includes(key)) {
        if (!dietary.includes(key)) return false;
      } else {
        if (allergens.includes(key.toLowerCase())) return false;
      }
    }
  }
  return true;
};

export default function MenuList({ items, filters }) {
  const grouped = {};
  items.forEach((item) => {
    const cat = item.Category || "Other";
    if (!grouped[cat]) grouped[cat] = [];
    if (matchesFilters(item, filters)) grouped[cat].push(item);
  });

  return (
    <div className="px-4 pb-20">
      {Object.entries(grouped).map(([cat, dishes]) => (
        <div key={cat} className="mb-6">
          <h2 className="text-xl font-bold text-gray-800 mb-3">{cat}</h2>
          <div className="space-y-4">
            {dishes.map((dish) => (
              <div
                key={dish.Name}
                className="border rounded-xl p-4 shadow-sm bg-white"
              >
                <div className="flex justify-between items-center">
                  <h3 className="text-lg font-semibold">{dish.Name}</h3>
                  <span className="text-sm font-medium text-gray-700">
                    ‚Ç¨{dish.Price}
                  </span>
                </div>
                <p className="text-sm text-gray-500">{dish.Ingredients}</p>

                {/* Dietary Tags */}
                {dish["Dietary Tags"] && (
                  <div className="text-sm mt-2">
                    <span className="font-medium text-green-700">üå± Dietary: </span>
                    {dish["Dietary Tags"]
                      .split(",")
                      .map((tag) => tag.trim().toLowerCase())
                      .map((tag) => (
                        <span key={tag} className="inline-block mr-2">
                          {dietaryIcons[tag] || ""} {tag}
                        </span>
                      ))}
                  </div>
                )}

                {/* Allergen Tags */}
                {dish.Allergens && dish.Allergens.toLowerCase() !== "none" && (
                  <div className="text-sm mt-1 text-red-700">
                    <span className="font-medium">‚ö†Ô∏è Allergens: </span>
                    {dish.Allergens
                      .split(",")
                      .map((a) => a.trim())
                      .map((a) => (
                        <span key={a} className="inline-block mr-2">
                          {allergenIcons[a] || "‚ùó"} {a}
                        </span>
                      ))}
                  </div>
                )}

                {/* Spice Level */}
                {dish["Spicy Level"] && dish["Spicy Level"] !== "None" && (
                  <div className="text-sm mt-1">üå∂Ô∏è {dish["Spicy Level"]}</div>
                )}

                {/* Notes */}
                {dish.Notes && (
                  <div className="text-xs text-gray-500 italic mt-1">{dish.Notes}</div>
                )}
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}
