import React from "react";

// Utility: Title case
const toTitleCase = (str) =>
  str
    ?.toLowerCase()
    .split(" ")
    .map((w) => w.charAt(0).toUpperCase() + w.slice(1))
    .join(" ");

const normalizeTags = (tagString = "") =>
  tagString
    .split(",")
    .map((tag) => tag.trim().toLowerCase())
    .filter(Boolean);

// ‚úÖ Dietary Tag Mapping
function getDietaryTags(item) {
  const tags = [];
  if (item.Vegan?.toLowerCase() === "yes") tags.push("vegan");
  if (item.Vegetarian?.toLowerCase() === "yes") tags.push("vegetarian");
  if (
    item["Gluten-Free"]?.toLowerCase() === "yes" ||
    item.GlutenFree?.toLowerCase() === "yes"
  )
    tags.push("glutenfree");
  if (item["Nut-Free"]?.toLowerCase() === "yes") tags.push("nutfree");
  if (item["Dairy-Free"]?.toLowerCase() === "yes") tags.push("dairyfree");
  if (item["Low Sugar"]?.toLowerCase() === "yes") tags.push("lowsugar");
  if (item["Halal"]?.toLowerCase() === "yes") tags.push("halal");
  return tags;
}

// ‚úÖ Icons
const getAllergenIcon = (tag) => {
  const icons = {
    milk: "ü•õ",
    eggs: "ü•ö",
    nuts: "ü•ú",
    peanuts: "ü•ú",
    gluten: "üåæ",
    soy: "üå±",
    fish: "üêü",
    crustaceans: "ü¶ê",
    molluscs: "üêö",
    celery: "ü•¨",
    mustard: "üåø",
    sesame: "‚ö™",
    lupin: "üå∏",
    sulphites: "üí®",
  };
  return icons[tag.toLowerCase()] || "‚ùó";
};

const getDietaryIcon = (tag) => {
  const icons = {
    vegan: "üå±",
    vegetarian: "ü•ï",
    glutenfree: "üö´üåæ",
    nutfree: "üö´ü•ú",
    dairyfree: "üö´ü•õ",
    lowsugar: "üç¨‚ùå",
    halal: "",
  };
  return icons[tag.toLowerCase()] || "‚úÖ";
};

// ‚úÖ Filter logic
const matchesFilters = (item, filters) => {
  const allergens = normalizeTags(item["Allergen Tags"]);
  const dietary = getDietaryTags(item);

  for (const tag of Object.keys(filters)) {
    if (!filters[tag]) continue;

    const tagLower = tag.toLowerCase();

    if (allergens.includes(tagLower)) return false;

    const isDietary = [
      "vegan",
      "vegetarian",
      "glutenfree",
      "nutfree",
      "dairyfree",
      "lowsugar",
      "halal",
    ];

    if (isDietary.includes(tagLower) && !dietary.includes(tagLower)) {
      return false;
    }
  }

  return true;
};

// üî• Spicy Level Icon Builder
const getSpicyIcons = (level = "") => {
  const map = {
    low: 1,
    medium: 2,
    hot: 3,
    "extra hot": 4,
  };
  const count = map[level.toLowerCase()] || 0;
  return "üå∂Ô∏è".repeat(count);
};

// ‚úÖ Main Render
const MenuList = ({ items = [], filters = {}, search = "", showHidden }) => {
  const grouped = {};

  items.forEach((item) => {
    const cat = toTitleCase(item.Category || "Uncategorized");
    if (!grouped[cat]) grouped[cat] = [];
    grouped[cat].push(item);
  });

  return (
    <div className="px-4 py-6 space-y-10">
      {Object.entries(grouped).map(([category, dishes]) => {
        const visibleDishes = dishes
          .map((item) => {
            const nameMatch = item["Dish Name"]
              ?.toLowerCase()
              .includes(search.toLowerCase());

            const passesFilter = matchesFilters(item, filters);
            const shouldShow = nameMatch && (passesFilter || showHidden);

            return {
              ...item,
              hidden: !passesFilter,
              _visible: shouldShow,
            };
          })
          .filter((item) => item._visible);

        if (visibleDishes.length === 0) return null;

        return (
          <div key={category}>
            <h2 className="text-xl font-semibold mb-3 text-center text-green-700">
              {category}
            </h2>
            <ul className="space-y-4">
              {visibleDishes.map((item, index) => (
                <li
                  key={index}
                  className={`p-4 border rounded-lg shadow-sm bg-white transition duration-200 ${
                    item.hidden ? "opacity-40 grayscale" : ""
                  }`}
                >
                  <h3 className="font-bold text-lg text-gray-800">
                    {item["Dish Name"]}
                  </h3>

                  {item.Ingredients && (
                    <p className="text-sm text-gray-700 mt-1">
                      {item.Ingredients}
                    </p>
                  )}

                  {item.Notes && (
                    <p className="italic text-xs text-gray-500 mt-1">
                      {item.Notes}
                    </p>
                  )}

                  {/* üî• Spicy Level */}
                  {item["Spicy Level"] && (
                    <div className="mt-2 text-sm text-orange-700">
                      <strong>Spice: </strong>
                      <span>{getSpicyIcons(item["Spicy Level"])}</span>
                    </div>
                  )}

                  {/* ‚ö†Ô∏è Allergens */}
                  {normalizeTags(item["Allergen Tags"]).length > 0 && (
                    <div className="mt-2 flex items-center gap-2 text-xs text-red-700">
                      <span className="font-semibold">‚ö†Ô∏è Allergens:</span>
                      <div className="flex flex-wrap gap-2">
                        {normalizeTags(item["Allergen Tags"]).map((a, i) => (
                          <span
                            key={`allergen-${i}`}
                            className="bg-red-100 px-2 py-1 rounded-full"
                          >
                            {getAllergenIcon(a)} {toTitleCase(a)}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* ‚úÖ Dietary */}
                  {getDietaryTags(item).length > 0 && (
                    <div className="mt-2 flex items-center gap-2 text-xs text-green-700">
                      <span className="font-semibold">Dietary:</span>
                      <div className="flex flex-wrap gap-2">
                        {getDietaryTags(item).map((d, i) => (
                          <span
                            key={`dietary-${i}`}
                            className="bg-green-100 px-2 py-1 rounded-full"
                          >
                            {getDietaryIcon(d)} {toTitleCase(d)}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}
                </li>
              ))}
            </ul>
          </div>
        );
      })}
    </div>
  );
};

export default MenuList;
