import React from "react";

// Updated dietary and allergen tags
const dietaryOptions = [
  { key: "vegan", label: "ðŸŒ± Vegan" },
  { key: "vegetarian", label: "ðŸ¥• Vegetarian" },
  { key: "glutenfree", label: "ðŸš«ðŸŒ¾ Gluten-Free" },
  { key: "nutfree", label: "ðŸš«ðŸ¥œ Nut-Free" },
  { key: "dairyfree", label: "ðŸš«ðŸ¥› Dairy-Free" },
  { key: "lowsugar", label: "ðŸ¬âŒ Low Sugar" },
  { key: "halal", label: "Halal" },
];

const allergens = [
  "milk", "eggs", "nuts", "peanuts", "gluten", "soy", "fish",
  "crustaceans", "molluscs", "celery", "mustard", "sesame",
  "lupin", "sulphites",
];

function toTitleCase(str) {
  return str
    ?.toLowerCase()
    .split(" ")
    .map((w) => w.charAt(0).toUpperCase() + w.slice(1))
    .join(" ");
}

const FilterPanel = ({ filters, setFilters, showHidden, setShowHidden }) => {
  const toggleFilter = (tag) => {
    setFilters((prev) => ({
      ...prev,
      [tag]: !prev[tag],
    }));
  };

  return (
    <div className="bg-white shadow rounded-lg p-4 mx-4 my-4">
      <h3 className="text-md font-semibold text-gray-700 mb-3">Filter Results</h3>

      {/* Allergen Filters */}
      <div className="mb-4">
        <p className="font-medium text-sm text-red-700 mb-1">Allergens</p>
        <div className="flex flex-wrap gap-2">
          {allergens.map((tag) => (
            <button
              key={tag}
              onClick={() => toggleFilter(tag)}
              className={`text-xs px-3 py-1 rounded-full border ${
                filters[tag] ? "bg-red-600 text-white" : "bg-red-100 text-red-800"
              }`}
            >
              {toTitleCase(tag)}
            </button>
          ))}
        </div>
      </div>

      {/* Dietary Filters */}
      <div>
        <p className="font-medium text-sm text-green-700 mb-1">Dietary Preferences</p>
        <div className="flex flex-wrap gap-2">
          {dietaryOptions.map(({ key, label }) => (
            <button
              key={key}
              onClick={() => toggleFilter(key)}
              className={`text-xs px-3 py-1 rounded-full border ${
                filters[key] ? "bg-green-600 text-white" : "bg-green-100 text-green-800"
              }`}
            >
              {label}
            </button>
          ))}
        </div>
      </div>

      {/* Show hidden */}
      <div className="mt-4">
        <label className="inline-flex items-center text-sm text-gray-600">
          <input
            type="checkbox"
            checked={showHidden}
            onChange={(e) => setShowHidden(e.target.checked)}
            className="form-checkbox h-4 w-4 mr-2 text-green-600"
          />
          Show hidden results (excluded by filters)
        </label>
      </div>
    </div>
  );
};

export default FilterPanel;
