// src/pages/App.jsx
import React, { useEffect, useState } from "react";
import Papa from "papaparse";
import SearchBar from "../components/SearchBar";
import FilterPanel from "../components/FilterPanel";
import MenuList from "../components/MenuList";
import QRCodeGenerator from "../components/QRCodeGenerator";

const MENU_URL =
  "https://docs.google.com/spreadsheets/d/e/2PACX-1vRfS9cag5vyojc7Gb2fGfFOmfiKZW8xnroajn69I66oWlcYf0a92QcarW2BpPyfGz7ZoI9zNpxzdZ6e/pub?output=csv";

const LOGO_URL =
  "https://docs.google.com/spreadsheets/d/e/2PACX-1vShydY4mFLVb8956vc5-7bIpGKd0iVG85JkiQ_wt3uLgvWRZabzpiU0vSIsdG24Jp16-zz9vfPb2gfv/pub?output=csv";

const CATEGORY_MAP = {
  food: ["Starters", "Mains", "Sides", "Desserts"],
  drinks: ["Drinks", "Cocktails", "Beers", "Wines", "Soft Drinks"],
  other: ["Other", "Specials", "Snacks"],
};

function App() {
  const [menu, setMenu] = useState([]);
  const [filteredMenu, setFilteredMenu] = useState([]);
  const [search, setSearch] = useState("");
  const [filters, setFilters] = useState({});
  const [showHidden, setShowHidden] = useState(false);
  const [location, setLocation] = useState("");
  const [logos, setLogos] = useState({});
  const [accepted, setAccepted] = useState(false);
  const [tab, setTab] = useState("food");
  const [availableLocations, setAvailableLocations] = useState([]);

  // Load menu from sheet
  useEffect(() => {
    Papa.parse(MENU_URL, {
      download: true,
      header: true,
      complete: (results) => {
        const cleaned = results.data.filter((item) => item["Dish Name"]);
        setMenu(cleaned);

        // Extract unique locations from menu
        const uniqueLocations = [
          ...new Set(cleaned.map((item) => item.Location?.toLowerCase().trim())),
        ].filter(Boolean);
        setAvailableLocations(uniqueLocations);

        // Auto-select the first available location
        if (!location && uniqueLocations.length > 0) {
          setLocation(uniqueLocations[0]);
        }
      },
    });
  }, []);

  // Load logo URLs from sheet
  useEffect(() => {
    Papa.parse(LOGO_URL, {
      download: true,
      header: true,
      complete: (results) => {
        const map = {};
        results.data.forEach((row) => {
          const loc = row.location?.trim().toLowerCase();
          const logo = row.logo_url?.trim();
          if (loc && logo) {
            map[loc] = logo;
          }
        });
        setLogos(map);
      },
    });
  }, []);

  // Filter menu by tab + location
  useEffect(() => {
    const allowedCategories = CATEGORY_MAP[tab] || [];
    const byCategory = menu.filter(
      (item) =>
        item.Location?.toLowerCase().trim() === location &&
        allowedCategories.includes(item.Category)
    );
    setFilteredMenu(byCategory);
  }, [menu, tab, location]);

  const logoToShow =
    logos[location] && logos[location].startsWith("http")
      ? logos[location]
      : "/logos/safebite.png";

  // Disclaimer screen
  if (!accepted) {
    return (
      <div className="h-screen flex items-center justify-center bg-white px-4 text-center">
        <div>
          <img
            src="/logos/safebite.png"
            alt="SafeBite"
            className="mx-auto h-20 mb-4"
          />
          <p className="mb-4 text-gray-700 text-lg max-w-lg">
            Welcome to SafeBite. This app displays allergen and dietary data
            per client. Confirm to proceed.
          </p>
          <button
            onClick={() => setAccepted(true)}
            className="px-5 py-2 bg-green-600 text-white rounded-lg shadow-md hover:bg-green-700"
          >
            Let's Go
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header with logo and location selector */}
      <header className="bg-white border-b py-4 shadow-sm text-center">
        <img
          src={logoToShow}
          alt="Client Logo"
          className="h-16 mx-auto object-contain"
        />
        <select
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="mt-2 px-3 py-1 border rounded-md shadow-sm"
        >
          {availableLocations.map((loc) => (
            <option key={loc} value={loc}>
              {loc.charAt(0).toUpperCase() + loc.slice(1)}
            </option>
          ))}
        </select>
      </header>

      {/* Tabs */}
      <div className="flex justify-center gap-4 bg-white py-3 border-b text-sm font-medium">
        {["food", "drinks", "other"].map((key) => (
          <button
            key={key}
            onClick={() => setTab(key)}
            className={`px-4 py-2 rounded-full transition ${
              tab === key
                ? "bg-green-600 text-white"
                : "bg-gray-100 text-gray-700 hover:bg-gray-200"
            }`}
          >
            {key.charAt(0).toUpperCase() + key.slice(1)}
          </button>
        ))}
      </div>

      {/* Main content */}
      <SearchBar search={search} setSearch={setSearch} />
      <FilterPanel
        filters={filters}
        setFilters={setFilters}
        showHidden={showHidden}
        setShowHidden={setShowHidden}
      />
      <MenuList
        items={filteredMenu}
        filters={filters}
        search={search}
        showHidden={showHidden}
      />
      <QRCodeGenerator />
    </div>
  );
}

export default App;
