import React from "react";

const dietaryIcons = {
  vegan: "ðŸŒ±",
  vegetarian: "ðŸ¥—",
  glutenfree: "ðŸš«ðŸŒ¾",
};

const allergenIcons = {
  Celery: "ðŸ¥¬",
  Gluten: "ðŸŒ¾",
  Crustaceans: "ðŸ¦",
  Eggs: "ðŸ¥š",
  Fish: "ðŸŸ",
  Lupin: "ðŸŒ¸",
  Milk: "ðŸ¥›",
  Molluscs: "ðŸš",
  Mustard: "ðŸŒ¿",
  Nuts: "ðŸŒ°",
  Peanuts: "ðŸ¥œ",
  Sesame: "âšª",
  Soy: "ðŸŒ±",
  Sulphites: "ðŸ’¨",
};

const allergensList = Object.keys(allergenIcons);

export default function FilterPanel({ filters, setFilters, safeOnly, setSafeOnly }) {
  const toggle = (key) => {
    setFilters((prev) => ({ ...prev, [key]: !prev[key] }));
  };

  return (
    <div className="bg-white border-y py-4 px-4 space-y-6 shadow-sm">
      {/* Dietary Filters */}
      <div>
        <h3 className="font-semibold text-gray-800 mb-2">Dietary Preferences</h3>
        <div className="flex gap-4 flex-wrap">
          {["vegan", "vegetarian", "glutenfree"].map((key) => (
            <label key={key} className="flex items-center gap-2 capitalize">
              <input
                type="checkbox"
                checked={filters[key]}
                onChange={() => toggle(key)}
              />
              <span>{dietaryIcons[key]} {key}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Allergen Filters */}
      <div>
        <h3 className="font-semibold text-gray-800 mb-2">Exclude Allergens</h3>
        <div className="flex gap-4 flex-wrap">
          {allergensList.map((key) => (
            <label key={key} className="flex items-center gap-2 capitalize text-sm">
              <input
                type="checkbox"
                checked={filters[key]}
                onChange={() => toggle(key)}
              />
              <span>{allergenIcons[key]} {key}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Safe-Only Toggle */}
      <div>
        <label className="flex items-center gap-2 text-red-600 font-semibold text-sm">
          <input
            type="checkbox"
            checked={safeOnly}
            onChange={() => setSafeOnly(!safeOnly)}
          />
          âœ… Only show safe dishes (based on your filters)
        </label>
      </div>
    </div>
  );
}
