// src/components/MenuList.jsx
import React from "react";

// Capitalize each word
const toTitleCase = (str) =>
  str
    ?.toLowerCase()
    .split(" ")
    .map((w) => w.charAt(0).toUpperCase() + w.slice(1))
    .join(" ");

// Converts "Milk, Eggs" → ["milk", "eggs"]
function normalizeTags(tagString = "") {
  return tagString
    .split(",")
    .map((tag) => tag.trim().toLowerCase())
    .filter(Boolean);
}

// Main filter logic
function matchesFilters(item, filters) {
  const allergens = normalizeTags(item.Allergens);
  const dietary = normalizeTags(item.Dietary);

  for (const tag of Object.keys(filters)) {
    if (!filters[tag]) continue;

    const tagLower = tag.toLowerCase();

    // If allergen tag is selected, item must NOT contain it
    if (allergens.includes(tagLower)) return false;

    // If dietary tag is selected (e.g. vegan), item MUST include it
    const isDietary = ["vegan", "vegetarian", "halal", "kosher", "gluten-free"];
    if (isDietary.includes(tagLower) && !dietary.includes(tagLower)) {
      return false;
    }
  }

  return true;
}

const MenuList = ({ items = [], filters = {}, search = "", showHidden }) => {
  // Group items by Category
  const grouped = {};

  items.forEach((item) => {
    const cat = toTitleCase(item.Category || "Uncategorized");
    if (!grouped[cat]) grouped[cat] = [];
    grouped[cat].push(item);
  });

  return (
    <div className="px-4 py-6 space-y-8">
      {Object.entries(grouped).map(([category, dishes]) => {
        const visibleDishes = dishes.filter((item) => {
          const nameMatch = item["Dish Name"]
            ?.toLowerCase()
            .includes(search.toLowerCase());
          const visible = showHidden || !item.hidden;
          return nameMatch && visible && matchesFilters(item, filters);
        });

        if (visibleDishes.length === 0) return null;

        return (
          <div key={category}>
            <h2 className="text-xl font-semibold mb-2 text-green-700">
              {category}
            </h2>
            <ul className="space-y-4">
              {visibleDishes.map((item, index) => (
                <li
                  key={index}
                  className={`p-4 border rounded-lg shadow-sm bg-white ${
                    item.hidden ? "opacity-40" : ""
                  }`}
                >
                  <h3 className="font-bold text-lg text-gray-800">
                    {item["Dish Name"]}
                  </h3>
                  {item.Description && (
                    <p className="text-sm text-gray-600 mt-1">
                      {item.Description}
                    </p>
                  )}

                  <div className="mt-2 flex flex-wrap gap-2 text-xs">
                    {normalizeTags(item.Allergens).map((a, i) => (
                      <span
                        key={i}
                        className="bg-red-100 text-red-700 px-2 py-1 rounded-full"
                      >
                        {toTitleCase(a)}
                      </span>
                    ))}
                    {normalizeTags(item.Dietary).map((d, i) => (
                      <span
                        key={i}
                        className="bg-green-100 text-green-700 px-2 py-1 rounded-full"
                      >
                        {toTitleCase(d)}
                      </span>
                    ))}
                  </div>
                </li>
              ))}
            </ul>
          </div>
        );
      })}
    </div>
  );
};

export default MenuList;
