import React from "react";

const ALLERGENS = [
  "Milk", "Eggs", "Nuts", "Peanuts", "Gluten", "Soy",
  "Fish", "Crustaceans", "Molluscs", "Celery", "Mustard",
  "Sesame", "Lupin", "Sulphites",
];

const dietaryOptions = [
  { key: "vegan", label: "üå± Vegan" },
  { key: "vegetarian", label: "ü•ï Vegetarian" },
  { key: "glutenfree", label: "üö´üåæ Gluten-Free" },
  { key: "nutfree", label: "üö´ü•ú Nut-Free" },
  { key: "dairyfree", label: "üö´ü•õ Dairy-Free" },
  { key: "lowsugar", label: "üç¨‚ùå Low Sugar" },
  { key: "halal", label: "Halal" },
];

const getIcon = (tag) => {
  const icons = {
    milk: "ü•õ", eggs: "ü•ö", nuts: "ü•ú", peanuts: "ü•ú",
    gluten: "üåæ", soy: "üå±", fish: "üêü", crustaceans: "ü¶ê",
    molluscs: "üêö", celery: "ü•¨", mustard: "üåø", sesame: "‚ö™",
    lupin: "üå∏", sulphites: "üí®",
    vegan: "üå±", vegetarian: "ü•ï", glutenfree: "üö´üåæ",
  };
  return icons[tag.toLowerCase()] || "‚ùó";
};

const toKey = (str) => str.toLowerCase().replace(/\s+/g, "");

const FilterPanel = ({ filters, setFilters, showHidden, setShowHidden }) => {
  const toggleTag = (tag) => {
    setFilters((prev) => ({
      ...prev,
      [tag]: !prev[tag],
    }));
  };

  return (
    <div className="px-4 mt-4">
      <div className="border border-gray-200 rounded-xl p-6 shadow-sm bg-white">
        {/* Allergen Filters */}
        <div className="mb-5 text-center">
          <p className="font-semibold text-lg text-red-700 mb-3">
            ‚ö†Ô∏è Allergen Filters
          </p>
          <div className="flex flex-wrap gap-3 justify-center">
            {ALLERGENS.map((tag) => {
              const key = toKey(tag);
              return (
                <button
                  key={key}
                  onClick={() => toggleTag(key)}
                  className={`px-4 py-2 rounded-full text-sm font-medium transition ${
                    filters[key]
                      ? "bg-red-600 text-white"
                      : "bg-red-100 text-red-700 hover:bg-red-200"
                  }`}
                >
                  {getIcon(key)} {tag}
                </button>
              );
            })}
          </div>
        </div>

        {/* Dietary Preferences */}
        <div className="mb-5 text-center">
          <p className="font-semibold text-lg text-green-700 mb-3">
            ü•¶ Dietary Preferences
          </p>
          <div className="flex flex-wrap gap-3 justify-center">
            {DIETARY.map((tag) => {
              const key = toKey(tag);
              return (
                <button
                  key={key}
                  onClick={() => toggleTag(key)}
                  className={`px-4 py-2 rounded-full text-sm font-medium transition ${
                    filters[key]
                      ? "bg-green-600 text-white"
                      : "bg-green-100 text-green-700 hover:bg-green-200"
                  }`}
                >
                  {getIcon(key)} {tag}
                </button>
              );
            })}
          </div>
        </div>

        {/* Show Hidden */}
        <div className="text-center">
          <button
            onClick={() => setShowHidden(!showHidden)}
            className={`px-4 py-2 text-sm rounded-full border transition font-medium ${
              showHidden
                ? "bg-gray-800 text-white"
                : "bg-gray-100 text-gray-700 hover:bg-gray-200"
            }`}
          >
            {showHidden ? "‚úî Showing Hidden" : "Show Hidden Results"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default FilterPanel;
