// src/utils/sheetsAPI.js
import axios from "axios";

const baseURL =
  "https://docs.google.com/spreadsheets/d/e/2PACX-1vRfS9cag5vyojc7Gb2fGfFOmfiKZW8xnroajn69I66oWlcYf0a92QcarW2BpPyfGz7ZoI9zNpxzdZ6e/pub?output=csv";

export async function fetchMenu(type) {
  const response = await axios.get(baseURL);
  const text = response.data;
  const [headerRow, ...rows] = text.split("\n").map((r) => r.split(","));
  const headers = headerRow.map((h) => h.trim());

  const entries = rows
    .map((row) =>
      Object.fromEntries(
        headers.map((h, i) => [h, row[i] ? row[i].trim() : ""])
      )
    )
    .filter((entry) => entry.Type === type); // Filter by "Food" or "Drinks"

  return entries;
}
