// src/App.jsx
import React, { useEffect, useState } from "react";
import Papa from "papaparse";
import SearchBar from "./components/SearchBar";
import FilterPanel from "./components/FilterPanel";
import MenuList from "./components/MenuList";
import QRCodeGenerator from "./components/QRCodeGenerator";

const SHEET_URL = "https://docs.google.com/spreadsheets/d/e/2PACX-1vRfS9cag5vyojc7Gb2fGfFOmfiKZW8xnroajn69I66oWlcYf0a92QcarW2BpPyfGz7ZoI9zNpxzdZ6e/pub?output=csv";

function App() {
  const [menu, setMenu] = useState([]);
  const [search, setSearch] = useState("");
  const [filters, setFilters] = useState({});
  const [showHidden, setShowHidden] = useState(false);
  const [location, setLocation] = useState("london");

  useEffect(() => {
    Papa.parse(SHEET_URL, {
      download: true,
      header: true,
      complete: (results) => {
        setMenu(results.data);
      },
    });
  }, []);

  const handleLocationChange = (e) => {
    setLocation(e.target.value);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white border-b py-4 shadow-sm text-center">
        <img
          src={`/logos/${location}.png`}
          alt="SafeBite Logo"
          className="h-16 mx-auto"
        />
        <select
          value={location}
          onChange={handleLocationChange}
          className="mt-2 px-3 py-1 border rounded-md shadow-sm"
        >
          <option value="london">London</option>
          <option value="paris">Paris</option>
          <option value="tokyo">Tokyo</option>
        </select>
      </header>

      {/* Search & Filter */}
      <SearchBar search={search} setSearch={setSearch} />
      <FilterPanel
        filters={filters}
        setFilters={setFilters}
        showHidden={showHidden}
        setShowHidden={setShowHidden}
      />

      {/* Menu Viewer */}
      <MenuList
        items={menu}
        filters={filters}
        search={search}
        showHidden={showHidden}
      />

      {/* QR Code */}
      <QRCodeGenerator />
    </div>
  );
}

export default App;
