import axios from "axios";

const baseURL = "https://docs.google.com/spreadsheets/d/e/2PACX-1vRfS9cag5vyojc7Gb2fGfFOmfiKZW8xnroajn69I66oWlcYf0a92QcarW2BpPyfGz7ZoI9zNpxzdZ6e/pub?output=csv";

export async function fetchMenu(tab) {
  // For simplicity, same URL used regardless of tab in this demo
  const response = await axios.get(baseURL);
  const text = response.data;
  const rows = text.split("\n").map(row => row.split(","));
  const headers = rows[0];
  const items = rows.slice(1).map(row =>
    Object.fromEntries(headers.map((h, i) => [h.trim(), row[i]?.trim() || ""]))
  );
  return items.filter(item => item.Type === tab);
}
