import React from "react";

const ALLERGEN_ICONS = {
  Gluten: "ðŸŒ¾",
  Crustaceans: "ðŸ¦",
  Eggs: "ðŸ¥š",
  Fish: "ðŸŸ",
  Peanuts: "ðŸ¥œ",
  Soybeans: "ðŸŒ±",
  Milk: "ðŸ¥›",
  Nuts: "ðŸŒ°",
  Celery: "ðŸ¥¬",
  Mustard: "ðŸŒ¿",
  Sesame: "ðŸ§‚",
  Sulphites: "ðŸ§ª",
  Lupin: "ðŸŒ¸",
  Molluscs: "ðŸš",
};

const DIETARY_ICONS = {
  Vegan: "ðŸŒ¿",
  Vegetarian: "ðŸ¥—",
  "Gluten-Free": "ðŸš«ðŸŒ¾",
};

function FilterPanel({ filters, setFilters, showHidden, setShowHidden }) {
  const allergenTags = Object.keys(ALLERGEN_ICONS);
  const dietaryTags = Object.keys(DIETARY_ICONS);

  const toggleFilter = (tag) => {
    setFilters((prev) => ({
      ...prev,
      [tag]: !prev[tag],
    }));
  };

  return (
    <div className="bg-white rounded-xl border border-gray-200 shadow-sm p-4 mb-4">
      <div className="mb-3">
        <p className="font-semibold text-gray-700 mb-2">Allergens</p>
        <div className="flex flex-wrap gap-2">
          {allergenTags.map((tag) => (
            <button
              key={tag}
              onClick={() => toggleFilter(tag)}
              className={`px-3 py-1 text-sm rounded-full border flex items-center gap-1 ${
                filters[tag]
                  ? "bg-red-500 text-white border-red-600"
                  : "bg-gray-100 text-gray-700 border-gray-300"
              }`}
            >
              <span>{ALLERGEN_ICONS[tag]}</span>
              <span>{tag}</span>
            </button>
          ))}
        </div>
      </div>

      <div className="mb-3">
        <p className="font-semibold text-gray-700 mb-2">Dietary</p>
        <div className="flex flex-wrap gap-2">
          {dietaryTags.map((tag) => (
            <button
              key={tag}
              onClick={() => toggleFilter(tag)}
              className={`px-3 py-1 text-sm rounded-full border flex items-center gap-1 ${
                filters[tag]
                  ? "bg-green-600 text-white border-green-700"
                  : "bg-gray-100 text-gray-700 border-gray-300"
              }`}
            >
              <span>{DIETARY_ICONS[tag]}</span>
              <span>{tag}</span>
            </button>
          ))}
        </div>
      </div>

      <div className="text-right mt-2">
        <button
          onClick={() => setShowHidden((prev) => !prev)}
          className="text-sm text-gray-600 underline"
        >
          {showHidden ? "Hide Excluded Items" : "Show Hidden Results"}
        </button>
      </div>
    </div>
  );
}

export default FilterPanel;
