// src/pages/App.jsx
import React, { useState, useEffect } from "react";
import DisclaimerModal from "../components/DisclaimerModal";
import LocationSelector from "../components/LocationSelector";
import MenuTabs from "../components/MenuTabs";
import FilterPanel from "../components/FilterPanel";
import MenuList from "../components/MenuList";
import Header from "../components/Header";
import SearchBar from "../components/SearchBar";
import QRCodeGenerator from "../components/QRCodeGenerator";
import { fetchMenu } from "../utils/sheetsAPI";

const App = () => {
  const [accepted, setAccepted] = useState(false);
  const [location, setLocation] = useState(null);
  const [menu, setMenu] = useState([]);
  const [search, setSearch] = useState("");
  const [safeOnly, setSafeOnly] = useState(false);
  const [filters, setFilters] = useState({
    vegan: false,
    vegetarian: false,
    glutenfree: false,
    Celery: false, Gluten: false, Crustaceans: false, Eggs: false,
    Fish: false, Lupin: false, Milk: false, Molluscs: false,
    Mustard: false, Nuts: false, Peanuts: false, Sesame: false,
    Soy: false, Sulphites: false
  });

  useEffect(() => {
    if (accepted && location) {
      fetchMenu().then(setMenu);
    }
  }, [accepted, location]);

  if (!accepted) return <DisclaimerModal onAccept={() => setAccepted(true)} />;
  if (!location) return <LocationSelector onSelect={setLocation} />;

  return (
    <div className="min-h-screen bg-gray-50">
      <Header location={location} />
      <SearchBar search={search} setSearch={setSearch} />
      <FilterPanel filters={filters} setFilters={setFilters} safeOnly={safeOnly} setSafeOnly={setSafeOnly} />
      <MenuList items={menu} filters={filters} search={search} safeOnly={safeOnly} />
      <QRCodeGenerator />
    </div>
  );
};

export default App;
