// src/components/FilterPanel.jsx
import React from "react";

const ALLERGENS = [
  "Gluten", "Crustaceans", "Eggs", "Fish", "Peanuts", "Soybeans", "Milk",
  "Nuts", "Celery", "Mustard", "Sesame", "Sulphites", "Lupin", "Molluscs"
];

const DIETARY = ["Vegan", "Vegetarian", "Gluten-Free"];

const ICONS = {
  Gluten: "âš ï¸",
  Crustaceans: "ðŸ¦",
  Eggs: "ðŸ¥š",
  Fish: "ðŸŸ",
  Peanuts: "ðŸ¥œ",
  Soybeans: "ðŸŒ±",
  Milk: "ðŸ¥›",
  Nuts: "ðŸŒ°",
  Celery: "ðŸ¥¬",
  Mustard: "ðŸŒ¿",
  Sesame: "ðŸŒ¾",
  Sulphites: "ðŸ§ª",
  Lupin: "ðŸŒ¸",
  Molluscs: "ðŸš",
  Vegan: "ðŸŒ±",
  Vegetarian: "ðŸ¥•",
  "Gluten-Free": "ðŸš«ðŸŒ¾"
};

const FilterPanel = ({ filters, setFilters, showHidden, setShowHidden }) => {
  const toggleFilter = (type, value) => {
    const updated = { ...filters };
    if (!updated[type]) updated[type] = [];
    if (updated[type].includes(value)) {
      updated[type] = updated[type].filter((v) => v !== value);
    } else {
      updated[type].push(value);
    }
    setFilters(updated);
  };

  const isSelected = (type, value) =>
    filters[type] && filters[type].includes(value);

  return (
    <div className="max-w-5xl mx-auto px-4">
      <div className="bg-white p-4 rounded-xl shadow-md mt-4">
        <div>
          <h2 className="text-lg font-semibold mb-2">Allergens</h2>
          <div className="flex flex-wrap gap-2">
            {ALLERGENS.map((allergen) => (
              <button
                key={allergen}
                onClick={() => toggleFilter("allergens", allergen)}
                className={`px-3 py-1 rounded-full border flex items-center gap-2 ${
                  isSelected("allergens", allergen)
                    ? "bg-red-100 border-red-400"
                    : "bg-gray-100 border-gray-300"
                }`}
              >
                <span>{ICONS[allergen]}</span>
                <span>{allergen}</span>
              </button>
            ))}
          </div>
        </div>
        <div className="mt-4">
          <h2 className="text-lg font-semibold mb-2">Dietary</h2>
          <div className="flex flex-wrap gap-2">
            {DIETARY.map((diet) => (
              <button
                key={diet}
                onClick={() => toggleFilter("dietary", diet)}
                className={`px-3 py-1 rounded-full border flex items-center gap-2 ${
                  isSelected("dietary", diet)
                    ? "bg-green-100 border-green-400"
                    : "bg-gray-100 border-gray-300"
                }`}
              >
                <span>{ICONS[diet]}</span>
                <span>{diet}</span>
              </button>
            ))}
          </div>
        </div>
        <div className="mt-4">
          <button
            onClick={() => setShowHidden((prev) => !prev)}
            className="text-sm text-blue-600 underline"
          >
            {showHidden ? "Hide blocked results" : "Show hidden results"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default FilterPanel;
