// src/components/MenuList.jsx
import React from "react";
import MenuItem from "./MenuItem";

const applyFilters = (item, filters, showHidden) => {
  // Allergen filters
  if (filters.allergens && filters.allergens.length > 0) {
    const itemAllergens = item["Allergen Tags"]?.toLowerCase() || "";
    const hasAllergen = filters.allergens.some((a) =>
      itemAllergens.includes(a.toLowerCase())
    );
    if (hasAllergen && !showHidden) return false;
  }

  // Dietary filters
  const dietChecks = {
    vegan: item["Vegan"]?.toLowerCase() === "yes",
    vegetarian: item["Vegetarian"]?.toLowerCase() === "yes",
    glutenfree: item["Gluten-Free"]?.toLowerCase() === "yes",
  };

  for (const [key, value] of Object.entries(filters)) {
    if (["vegan", "vegetarian", "glutenfree"].includes(key) && value && !dietChecks[key]) {
      return false;
    }
  }

  return true;
};

const applySearch = (item, search) => {
  const terms = search.toLowerCase();
  return (
    item["Dish Name"]?.toLowerCase().includes(terms) ||
    item["Ingredients"]?.toLowerCase().includes(terms) ||
    item["Category"]?.toLowerCase().includes(terms)
  );
};

const MenuList = ({ items, filters, search, showHidden }) => {
  const groupedItems = items.reduce((acc, item) => {
    if (!applySearch(item, search) || !applyFilters(item, filters, showHidden)) return acc;

    const category = item["Category"] || "Other";
    if (!acc[category]) acc[category] = [];
    acc[category].push(item);
    return acc;
  }, {});

  return (
    <div className="px-4 py-6">
      {Object.keys(groupedItems).length === 0 ? (
        <p className="text-gray-500">No dishes match your criteria.</p>
      ) : (
        Object.entries(groupedItems).map(([category, dishes]) => (
          <div key={category} className="mb-6">
            <h2 className="text-xl font-semibold mb-3">{category}</h2>
            <div className="grid gap-4 sm:grid-cols-2">
              {dishes.map((dish, index) => (
                <MenuItem key={index} item={dish} />
              ))}
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default MenuList;
