import React from "react";

function FilterPanel({ filters, setFilters, showHidden, setShowHidden }) {
  const allergenTags = [
    "Gluten", "Crustaceans", "Eggs", "Fish", "Peanuts",
    "Soybeans", "Milk", "Nuts", "Celery", "Mustard",
    "Sesame", "Sulphites", "Lupin", "Molluscs",
  ];

  const dietaryTags = ["Vegan", "Vegetarian", "Gluten-Free"];

  const toggleFilter = (tag) => {
    setFilters((prev) => ({
      ...prev,
      [tag]: !prev[tag],
    }));
  };

  return (
    <div className="bg-white rounded-xl border border-gray-200 shadow-sm p-4 mb-4">
      <div className="mb-3">
        <p className="font-semibold text-gray-700 mb-2">Allergens</p>
        <div className="flex flex-wrap gap-2">
          {allergenTags.map((tag) => (
            <button
              key={tag}
              onClick={() => toggleFilter(tag)}
              className={`px-3 py-1 text-sm rounded-full border ${
                filters[tag]
                  ? "bg-red-500 text-white border-red-600"
                  : "bg-gray-100 text-gray-700 border-gray-300"
              }`}
            >
              âš  {tag}
            </button>
          ))}
        </div>
      </div>

      <div className="mb-3">
        <p className="font-semibold text-gray-700 mb-2">Dietary</p>
        <div className="flex flex-wrap gap-2">
          {dietaryTags.map((tag) => (
            <button
              key={tag}
              onClick={() => toggleFilter(tag)}
              className={`px-3 py-1 text-sm rounded-full border ${
                filters[tag]
                  ? "bg-green-600 text-white border-green-700"
                  : "bg-gray-100 text-gray-700 border-gray-300"
              }`}
            >
              ðŸŒ± {tag}
            </button>
          ))}
        </div>
      </div>

      <div className="text-right mt-2">
        <button
          onClick={() => setShowHidden((prev) => !prev)}
          className="text-sm text-gray-600 underline"
        >
          {showHidden ? "Hide Excluded Items" : "Show Hidden Results"}
        </button>
      </div>
    </div>
  );
}

export default FilterPanel;
