import React from "react";

const ALLERGENS = [
  "Milk",
  "Eggs",
  "Nuts",
  "Peanuts",
  "Gluten",
  "Soy",
  "Fish",
  "Crustaceans",
  "Molluscs",
  "Celery",
  "Mustard",
  "Sesame",
  "Lupin",
  "Sulphites",
];

const DIETARY = ["Vegan", "Vegetarian", "GlutenFree"];

const getIcon = (tag) => {
  const icons = {
    milk: "ü•õ",
    eggs: "ü•ö",
    nuts: "ü•ú",
    peanuts: "ü•ú",
    gluten: "üåæ",
    soy: "üå±",
    fish: "üêü",
    crustaceans: "ü¶ê",
    molluscs: "üêö",
    celery: "ü•¨",
    mustard: "üåø",
    sesame: "‚ö™",
    lupin: "üå∏",
    sulphites: "üí®",
    vegan: "üå±",
    vegetarian: "ü•ï",
    glutenfree: "üö´üåæ",
  };
  return icons[tag.toLowerCase()] || "‚ùó";
};

const toKey = (str) => str.toLowerCase().replace(/\s+/g, "");

const FilterPanel = ({ filters, setFilters, showHidden, setShowHidden }) => {
  const toggleTag = (tag) => {
    setFilters((prev) => ({
      ...prev,
      [tag]: !prev[tag],
    }));
  };

  return (
    <div className="px-4 mt-4">
      <div className="border border-gray-200 rounded-lg p-4 shadow-sm bg-white">
        {/* Allergen Filters */}
        <div className="mb-3">
          <p className="font-semibold text-sm text-gray-700 mb-2">
            ‚ö†Ô∏è Allergen Filters
          </p>
          <div className="flex flex-wrap gap-2">
            {ALLERGENS.map((tag) => {
              const key = toKey(tag);
              return (
                <button
                  key={key}
                  onClick={() => toggleTag(key)}
                  className={`px-3 py-1 rounded-full text-xs ${
                    filters[key]
                      ? "bg-red-600 text-white"
                      : "bg-red-100 text-red-700"
                  }`}
                >
                  {getIcon(key)} {tag}
                </button>
              );
            })}
          </div>
        </div>

        {/* Dietary Filters */}
        <div className="mb-3">
          <p className="font-semibold text-sm text-gray-700 mb-2">
            ü•¶ Dietary Preferences
          </p>
          <div className="flex flex-wrap gap-2">
            {DIETARY.map((tag) => {
              const key = toKey(tag);
              return (
                <button
                  key={key}
                  onClick={() => toggleTag(key)}
                  className={`px-3 py-1 rounded-full text-xs ${
                    filters[key]
                      ? "bg-green-600 text-white"
                      : "bg-green-100 text-green-700"
                  }`}
                >
                  {getIcon(key)} {tag}
                </button>
              );
            })}
          </div>
        </div>

        {/* Show Hidden Button */}
        <div className="mt-2">
          <button
            onClick={() => setShowHidden(!showHidden)}
            className={`px-4 py-1 text-xs rounded-full border transition ${
              showHidden
                ? "bg-gray-800 text-white"
                : "bg-gray-100 text-gray-700 hover:bg-gray-200"
            }`}
          >
            {showHidden ? "‚úî Show Hidden Enabled" : "Show Hidden Results"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default FilterPanel;
