// src/App.jsx
import React, { useEffect, useState } from "react";
import Papa from "papaparse";
import SearchBar from "./components/SearchBar";
import FilterPanel from "./components/FilterPanel";
import MenuList from "./components/MenuList";
import QRCodeGenerator from "./components/QRCodeGenerator";

const MENU_URL =
  "https://docs.google.com/spreadsheets/d/e/2PACX-1vRfS9cag5vyojc7Gb2fGfFOmfiKZW8xnroajn69I66oWlcYf0a92QcarW2BpPyfGz7ZoI9zNpxzdZ6e/pub?output=csv";

const LOGO_URL =
  "https://docs.google.com/spreadsheets/d/e/2PACX-1vShydY4mFLVb8956vc5-7bIpGKd0iVG85JkiQ_wt3uLgvWRZabzpiU0vSIsdG24Jp16-zz9vfPb2gfv/pub?output=csv";

function App() {
  const [menu, setMenu] = useState([]);
  const [search, setSearch] = useState("");
  const [filters, setFilters] = useState({});
  const [showHidden, setShowHidden] = useState(false);
  const [location, setLocation] = useState("london");
  const [logos, setLogos] = useState({});

  // Load menu from Google Sheets
  useEffect(() => {
    Papa.parse(MENU_URL, {
      download: true,
      header: true,
      complete: (results) => setMenu(results.data),
    });
  }, []);

  // Load logos from another sheet
  useEffect(() => {
    Papa.parse(LOGO_URL, {
      download: true,
      header: true,
      complete: (results) => {
        const map = {};
        results.data.forEach((row) => {
          const loc = row.location?.trim().toLowerCase();
          const logo = row.logo_url?.trim();
          if (loc && logo) {
            map[loc] = logo;
          }
        });
        setLogos(map);
      },
    });
  }, []);

  const currentLogo =
    logos[location] && logos[location].startsWith("http")
      ? logos[location]
      : "/logos/safebite.png"; // local fallback logo

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white border-b py-4 shadow-sm text-center">
        <img
          src={currentLogo}
          alt="Logo"
          className="h-16 mx-auto object-contain"
        />
        <select
          value={location}
          onChange={(e) => setLocation(e.target.value)}
          className="mt-2 px-3 py-1 border rounded-md shadow-sm"
        >
          {Object.keys(logos).map((loc) => (
            <option key={loc} value={loc}>
              {loc.charAt(0).toUpperCase() + loc.slice(1)}
            </option>
          ))}
        </select>
      </header>

      <SearchBar search={search} setSearch={setSearch} />
      <FilterPanel
        filters={filters}
        setFilters={setFilters}
        showHidden={showHidden}
        setShowHidden={setShowHidden}
      />
      <MenuList
        items={menu}
        filters={filters}
        search={search}
        showHidden={showHidden}
      />
      <QRCodeGenerator />
    </div>
  );
}

export default App;
